<script>
    function shousuo(){
        var status = $(".shousuo").css('display');
        if(status == 'block'){
            $(".shousuo").css('display','none');
            $(".see_more").text('查看更多信息')
        }else{
            $(".shousuo").css('display','block');
            $(".see_more").text('收起');

        }
    }

    $(".van-collapse-item__title").on('click',function(){
        console.log(123);
        var status = $(this).next().css('display');
        if(status == 'block'){
            $(this).next().css('display','none');
            $(this).children('.van-cell__right-icon').rotate({animateTo:0});
            // console.log($('.van-cell__right-icon').rotate({animateTo:0}));
        }else{
            $(this).next().css('display','block');
            // console.log($('.van-cell__right-icon').rotate({animateTo:-180}));

            // $(this).children('.van-cell__right-icon').rotate(90);

            $(this).children('.van-cell__right-icon').rotate({animateTo:-180});

        }
    });

</script>


